import React from 'react';
import { useTable } from 'react-table';
import './Listagem.css';


const Listagem = ({ simulacoes }) => {
  console.log('Dados recebidos:', simulacoes);

  // Extrai as colunas da primeira simulação para gerar as colunas da tabela
  const colunas = React.useMemo(
    () =>
      simulacoes.length > 0
        ? Object.keys(simulacoes[0]).map(chave => ({
            Header: chave,
            accessor: chave,
          }))
        : [],
    [simulacoes]
  );

  // Extrai os dados das simulações
  const dados = React.useMemo(() => simulacoes, [simulacoes]);

  // Instancia a tabela com as colunas e dados
  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = useTable({
    columns: colunas,
    data: dados,
  });

  return (
    <table {...getTableProps()} style={{ borderCollapse: 'collapse', width: '100%' }}>
    <thead>
        {headerGroups.map(headerGroup => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map(coluna => (
              <th {...coluna.getHeaderProps()} style={{ border: '1px solid black', padding: '8px' }}>
                {coluna.render('Header')}
              </th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map(linha => {
          prepareRow(linha);
          return (
            <tr {...linha.getRowProps()}>
              {linha.cells.map(celula => (
                <td {...celula.getCellProps()} style={{ border: '1px solid black', padding: '8px' }}>
                  {celula.render('Cell')}
                </td>
              ))}
            </tr>
          );
        })}
      </tbody>
    </table>
  );
};

export default Listagem;
