// Listagem.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Listagem = () => {
  const [simulacoes, setSimulacoes] = useState([]);

  useEffect(() => {
    const fetchSimulacoes = async () => {
      try {
        // Fazendo a solicitação GET à API para listar as simulações
        const response = await axios.get('http://localhost:3000/api/listar-simulacoes');
        setSimulacoes(response.data);
      } catch (error) {
        console.error('Erro ao buscar simulações:', error);
      }
    };

    fetchSimulacoes();
  }, []);

  // Exibindo o array no console
  console.log('Array de Simulações:', simulacoes);

  const renderSimulacoes = () => {
    const simulacoesList = [];

    for (let i = 0; i < simulacoes.length; i++) {
      const simulacao = simulacoes[i];
      simulacoesList.push(
        <li key={simulacao.id}>
          <p>Nome: {simulacao.nomeCompleto}</p>
          <p>Email: {simulacao.email}</p>
          <p>Telefone: {simulacao.telefone}</p>
          {/* Adicione mais campos conforme necessário */}
        </li>
      );
    }

    return simulacoesList;
  };

  return (
    <div>
      <h1>Listagem de Simulações</h1>
      <ul>{renderSimulacoes()}</ul>
    </div>
  );
};

export default Listagem;
