import React, { useEffect, useRef } from 'react';
import 'tabulator-tables/dist/css/tabulator.min.css';
import 'tabulator-tables/dist/js/tabulator.min.js';

const Listagem = ({ simulacoes }) => {
  const tableRef = useRef(null);

  useEffect(() => {
    // Configuração da tabela
    const table = new window.Tabulator(tableRef.current, {
      data: simulacoes,
      layout: 'fitDataFill',
      columns: [
        { title: 'ID', field: 'id' },
        { title: 'Nome', field: 'nomeCompleto' },
        { title: 'Email', field: 'email' },
        { title: 'Telefone', field: 'telefone' },
        { title: 'Código da Unidade', field: 'unidades[0].codigoDaUnidadeConsumidora' },
        { title: 'Modelo Fásico', field: 'unidades[0].modeloFasico' },
        { title: 'Enquadramento', field: 'unidades[0].enquadramento' },
        { title: 'Histórico de Consumo em kWh', field: 'unidades[0].historicoDeConsumoEmKWH[0].consumoForaPontaEmKWH' },
      ],
      // Opções de estilo
      tooltips: true,
      pagination: 'local',
      paginationSize: 10,
      movableColumns: true,
    });

    return () => {
      // Destruir a tabela ao desmontar o componente para evitar vazamentos de memória
      table.destroy();
    };
  }, [simulacoes]);

  return (
    <div>
      <h2>Listagem de Simulações</h2>
      <div ref={tableRef}></div>
    </div>
  );
};

export default Listagem;
