import React, { useState, useEffect } from 'react';
import Formulario from './components/Formulario';
import Listagem from './components/Listagem'; // Importe a Listagem, ajuste conforme necessário
import { registrarSimulacao, obterSimulacoes } from './services/api';
import './App.css';

function App() {
  const [simulacoes, setSimulacoes] = useState([]);
  const [feedback, setFeedback] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSimulacoes = async () => {
      try {
        const simulacoesObtidas = await obterSimulacoes();
        setSimulacoes(simulacoesObtidas);
      } catch (error) {
        setError('Erro ao obter simulações. Por favor, tente novamente.');
        console.error('Erro ao obter simulações:', error);
      }
    };

    fetchSimulacoes();
  }, []); 

  const handleSuccess = (mensagem) => {
    setFeedback(mensagem);
    setError(null);
  };

  const handleError = (mensagem) => {
    setError(mensagem);
    setFeedback(null);
  };

  const handleSubmit = async (dadosFormulario) => {
    try {
      const simulacaoRegistrada = await registrarSimulacao(dadosFormulario);
      setSimulacoes([...simulacoes, simulacaoRegistrada]);
      handleSuccess('Simulação registrada com sucesso!');
    } catch (error) {
      handleError('Erro ao registrar simulação. Por favor, tente novamente.');
      console.error('Erro ao registrar simulação:', error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <Formulario onSubmit={handleSubmit} />
        {feedback && <p className="feedback success">{feedback}</p>}
        {error && <p className="feedback error">{error}</p>}
        <Listagem simulacoes={simulacoes} />
      </header>
    </div>
  );
}

export default App;
