import React, { useState, useEffect } from 'react';
import Formulario from './components/Formulario';
import ListarSimulacoes from './components/Listagem';
import { registrarSimulacao, obterSimulacoes } from './services/api';
import './App.css';

function App() {
  const [mostrarListagem, setMostrarListagem] = useState(false);
  const [simulacoes, setSimulacoes] = useState([]);

  useEffect(() => {
    const fetchSimulacoes = async () => {
      try {
        const simulacoesObtidas = await obterSimulacoes();
        setSimulacoes(simulacoesObtidas);
      } catch (error) {
        // Trate o erro, se necessário
      }
    };

    fetchSimulacoes();
  }, []); // Executado apenas uma vez após a montagem do componente

  const handleSubmit = async (dadosFormulario) => {
    try {
      const simulacaoRegistrada = await registrarSimulacao(dadosFormulario);
      setSimulacoes([...simulacoes, simulacaoRegistrada]);
      setMostrarListagem(true);
    } catch (error) {
      // Trate o erro, se necessário
    }
  };

  const handleVoltar = () => {
    setMostrarListagem(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="layout-container">
          <div className="half-width">
            <ListarSimulacoes simulacoes={simulacoes} onVoltar={handleVoltar} />
          </div>
          <div className="half-width">
            <Formulario onSubmit={handleSubmit} />
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
